        type: 'warning'
      }).then(function () {
        orderServer.copyOfferByOfferId(row.id).then(function (res) {
          if (res.code === 200) {
            _this6.$notify({
              title: '成功',
              message: res.message,
              type: 'success'
            });

            _this6.handleSearch();
