    handleUpdate: function handleUpdate(row) {
      var _this7 = this;

      this.orderForm.offerId = row.id;
      this.orderDialogStatus = 'edit';
      orderServer.findOfferDetailsByOfferId(row.id).then(function (res) {
        if (res.code === 200) {
          _this7.orderForm.offerId = res.result.offerId || '';
          _this7.orderForm.offerInfoTdo = res.result.offerBaseVo || {};
          _this7.orderForm.offerCostTdo = res.result.costVo || {};
          _this7.orderForm.selfCostTdos = res.result.selfCostVos || [];
          _this7.orderForm.offerPlanTdos = res.result.offerPlanVos.map(function (i) {
            if (i.planFactorVoList && i.planFactorVoList.length > 0) {
              i.planFactorVoList.forEach(function (j) {
                if (j.dynamicParam !== '{}' && j.dynamicParam.indexOf('meanName') > -1) {
                  var dynParam = JSON.parse(j.dynamicParam);

                  _this7.$set(j, 'meanName', dynParam.meanName);
                }

                var subCheckStandardIds = [];

                if (j.factorSubSetVos && j.factorSubSetVos.length > 0) {
                  subCheckStandardIds = j.factorSubSetVos.map(function (m) {
                    return m.checkStandardId;
                  });
                }

                _this7.$set(j, 'subCheckStandardIds', subCheckStandardIds);
              });

              _this7.$set(i, 'factorTdoList', i.planFactorVoList);
