    getPlanSummaries: function getPlanSummaries(param) {
      var columns = param.columns,
          data = param.data;
      var sums = [];
      columns.forEach(function (column, index) {
        if (index === 0) {
          sums[index] = '合计';
          return;
        }

        var values = data.map(function (item) {
          if (column.label === '次数') {
            return Number(item.dayCount * item.frequency);
          } else if (column.label === '总计') {
            return parseFloat(item.dayCount * item.frequency * item.costPerTime);
          } else {
            return Number(item[column.property]);
          }
        });

        if (!values.every(function (value) {
          return isNaN(value);
        })) {
          if (index === 6) {
            sums[index] = values.reduce(function (prev, curr) {
              var value = Number(curr);

              if (!isNaN(value)) {
                return prev + curr;
              } else {
                return prev;
              }
            }, 0);
          } else if (index === 8) {
            sums[index] = values.reduce(function (prev, curr) {
              var value = Number(curr);

              if (!isNaN(value)) {
                return prev + curr;
              } else {
                return prev;
              }
            }, 0);
          } else if (index === 10) {
            sums[index] = values.reduce(function (prev, curr) {
              var value = Number(curr);

              if (!isNaN(value)) {
                return prev + curr;
              } else {
                return prev;
              }
            }, 0).toFixed(2);
          }
        } else {
          sums[index] = '';
        }
      });
      return sums;
    },
