.app-main {
  /*  50 = navbar的 50高度 */
  min-height: calc(100vh - 50px);
  width: 100%;
  margin: 8px 0;
  padding: 0px 10px;
  position: relative;
  overflow: hidden;
  background: rgb(240, 242, 245);
}

.fixed-header+.app-main{
  padding-top: 50px;
}

.hasTagsView {
  .app-main {
    // min-height: calc(100vh - 84px);
    min-height: calc(100vh);
  }

  .fixed-header+.app-main {
    padding-top: 92px;
  }
}
